from textwrap import dedent

import pytest
from unittest.mock import MagicMock, call

from neo4j import Result

from models.node import Node
from storage.graph_storage import GraphStorage


@pytest.fixture
def graph_storage():
    # Mock the Neo4j driver
    driver_mock = MagicMock()

    # Create a mock session
    session_mock = MagicMock()

    # Configure the session to behave as a context manager
    driver_mock.session.return_value = session_mock
    session_mock.__enter__.return_value = session_mock
    session_mock.__exit__.return_value = None

    return GraphStorage(driver_mock)


def assert_node(node, expected_uuid, expected_level, expected_content, expected_children):
    assert node.uuid == expected_uuid
    assert node.level == expected_level
    assert node.content == expected_content
    assert len(node.children) == len(expected_children)
    for i, child in enumerate(expected_children):
        assert node.children[i].uuid == child['uuid']
        assert node.children[i].level == child['level']
        assert node.children[i].content == child['content']


def test_retrieve_hierarchy_single_node(graph_storage):
    root_uuid = "root-uuid"

    # Mock the return value to include the root node
    mock_data = {
        'root_uuid': root_uuid,
        'root_level': 'root',
        'root_content': 'Root Node',
        'relationships': []
    }

    # Configure the mock to return the mock_data
    session_mock = graph_storage.driver.session.return_value.__enter__.return_value
    session_mock.run.return_value.single.return_value = mock_data

    # Call the method
    node = graph_storage.retrieve_hierarchy(root_uuid)

    # Assertions
    assert_node(node, root_uuid, 'root', 'Root Node', [])


def test_retrieve_hierarchy_with_children(graph_storage):
    root_uuid = "root-uuid"
    child_uuid_1 = "child-uuid-1"
    child_uuid_2 = "child-uuid-2"

    # Mock the return value to include the root and child nodes
    mock_data = {
        'root_uuid': root_uuid,
        'root_level': 'root',
        'root_content': 'Root Node',
        'relationships': [
            {
                'parent': root_uuid,
                'self': child_uuid_1,
                'contents': 'Child Node 1',
                'level': 'child',
                'order': 0
            },
            {
                'parent': root_uuid,
                'self': child_uuid_2,
                'contents': 'Child Node 2',
                'level': 'child',
                'order': 1
            }
        ]
    }

    # Configure the mock to return the mock_data
    session_mock = graph_storage.driver.session.return_value.__enter__.return_value
    session_mock.run.return_value.single.return_value = mock_data

    # Call the method
    node = graph_storage.retrieve_hierarchy(root_uuid)

    # Assertions
    expected_children = [
        {'uuid': child_uuid_1, 'level': 'child', 'content': 'Child Node 1'},
        {'uuid': child_uuid_2, 'level': 'child', 'content': 'Child Node 2'}
    ]
    assert_node(node, root_uuid, 'root', 'Root Node', expected_children)


class TestRoundTrip:
    """
    Tests round trip to GraphStorage, storage and retrieval
    """

    def test_batch_store_with_children(self, graph_storage):
        # Create a root node with children
        root_node = Node(uuid="root-uuid", level="root", content="Root Node", children=[
            Node(uuid="child-uuid-1", level="child", content="Child Node 1"),
            Node(uuid="child-uuid-2", level="child", content="Child Node 2")
        ])

        # Batch store the node hierarchy
        graph_storage.batch_store([root_node])

        # Assert the insert commands
        session_mock = graph_storage.driver.session.return_value.__enter__.return_value
        expected_calls = [
            call(
                dedent(
                    """
                        MERGE (n:Node {uuid: $uuid})
                        SET n.level = $level, n.content = $content, n.ordinal = $ordinal
                    """
                ),
                uuid="root-uuid", level="root", content="Root Node", ordinal=None
            ),
            call(
                dedent(
                    """
                        MERGE (n:Node {uuid: $uuid})
                        SET n.level = $level, n.content = $content, n.ordinal = $ordinal
                    """
                ),
                uuid="child-uuid-1", level="child", content="Child Node 1", ordinal=0
            ),
            call(
                dedent(
                    """
                        MERGE (n:Node {uuid: $uuid})
                        SET n.level = $level, n.content = $content, n.ordinal = $ordinal
                    """
                ),
                uuid="child-uuid-2", level="child", content="Child Node 2", ordinal=1
            ),
        ]
        session_mock.run.assert_has_calls(expected_calls, any_order=True)

    def test_retrieve_hierarchy_with_children(self, graph_storage):
        root_uuid = "root-uuid"
        child_uuid_1 = "child-uuid-1"
        child_uuid_2 = "child-uuid-2"

        # Mock the return value to include the root and child nodes
        mock_data = {
            'root_uuid': root_uuid,
            'root_level': 'root',
            'root_content': 'Root Node',
            'relationships': [
                {
                    'parent': root_uuid,
                    'self': child_uuid_1,
                    'contents': 'Child Node 1',
                    'level': 'child',
                    'order': 0
                },
                {
                    'parent': root_uuid,
                    'self': child_uuid_2,
                    'contents': 'Child Node 2',
                    'level': 'child',
                    'order': 1
                }
            ]
        }

        # Configure the mock to return the mock_data
        session_mock = graph_storage.driver.session.return_value.__enter__.return_value
        session_mock.run.return_value.single.return_value = mock_data

        # Retrieve the node hierarchy
        retrieved_node = graph_storage.retrieve_hierarchy(root_uuid)

